  //Ovo je f-ja za prikazivanje, zato je stavljen cartIsShown na true
  const showCardHandler = () => {
    setCartIsShown(true);
  };
  //Ovo je f-ja za sakrivanje
  const hideCardHandler = () => {
    setCartIsShown(false);
  };

  return (
    <React.Fragment>
      <Cart />
      <Header />
      <main>
        <Meals />
      </main>
    </React.Fragment>
  );
}

export default App;
